  |> Enum.with_index
  |> Enum.reduce_while(0, fn({x, index}, acc) ->
    if acc < 0 do
      {:halt, index}
    else
      case x do
          "(" ->
              {:cont, acc + 1}
          ")" ->
              {:cont, acc - 1}
      end
    end
  end)
  |> IO.inspect
